import { Container, Nav, Navbar } from "react-bootstrap";
import React, { useState } from 'react';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';
import { Link, useLocation } from "react-router-dom";

function NavigationBar() {
    const location = useLocation();
    const [checked, setChecked] = useState(false);
    const [radioValue, setRadioValue] = useState('1');

    if(location.pathname === "/") {
        return null;
    }
    /*
    return (
        <Navbar bg="primary" variant="primary" expand="lg">
            <Container>
                <Navbar.Brand>SisCAPACIT 1.0</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <Link className="btn btn-outline-light btn-lg" role="button" to="/">HomePage</Link>
                        <Link className="btn btn-outline-light btn-lg" role="button" to="/funcionarios">Servidores</Link>
                        <Link className="btn btn-outline-light btn-lg" role="button" to="/cadastrar">Cursos</Link>
                        <Link className="btn btn-outline-light btn-lg" role="button" to="/">Relatórios</Link>
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    ) */

  const radios = [
    { name: 'Active', value: '1' },
    { name: 'Radio', value: '2' },
    { name: 'Radio', value: '3' },
  ];

  return (
    <>
      <ButtonGroup className="mb-2">
        <ToggleButton
          id="toggle-check"
          type="checkbox"
          variant="secondary"
          checked={checked}
          value="1"
          onChange={(e) => setChecked(e.currentTarget.checked)}
        >
          Checked
        </ToggleButton>
      </ButtonGroup>
      <br />
      <ButtonGroup className="mb-2">
        {radios.map((radio, idx) => (
          <ToggleButton
            key={idx}
            id={`radio-${idx}`}
            type="radio"
            variant="secondary"
            name="radio"
            value={radio.value}
            checked={radioValue === radio.value}
            onChange={(e) => setRadioValue(e.currentTarget.value)}
          >
            {radio.name}
          </ToggleButton>
        ))}
      </ButtonGroup>
      <br />
      <ToggleButton
        className="mb-2"
        id="toggle-check"
        type="checkbox"
        variant="outline-primary"
        checked={checked}
        value="1"
        onChange={(e) => setChecked(e.currentTarget.checked)}
      >
        Checked
      </ToggleButton>
      <br />
      <ButtonGroup>
        {radios.map((radio, idx) => (
          <ToggleButton
            key={idx}
            id={`radio-${idx}`}
            type="radio"
            variant={idx % 2 ? 'outline-success' : 'outline-danger'}
            name="radio"
            value={radio.value}
            checked={radioValue === radio.value}
            onChange={(e) => setRadioValue(e.currentTarget.value)}
          >
            {radio.name}
          </ToggleButton>
        ))}
      </ButtonGroup>
    </>
  );
}


export default NavigationBar;
