import { Container, Nav, Navbar } from "react-bootstrap";
import { Link, useLocation } from "react-router-dom";

function NavigationBar() {
    const location = useLocation()

    if(location.pathname === "/") {
        return null
    }

    return (
        <Navbar bg="primary" variant="primary" expand="lg">
            <Container>
                <Navbar.Brand>SisCAPACIT 1.0</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <Link className="nav-link bg-white" to="/" >>> HomePage</Link>
                        <Link className="nav-link" to="/funcionarios" onClick={(e)=>{e.target.style.backgroundColor ="bg-white"}}>>> Servidores</Link>
                        <Link className="nav-link" to="/cadastrar">>> Cursos</Link>
                        <Link className="btn btn-outline-light btn-lg" role="button" to="/about">HomePage</Link>
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    )
}

export default NavigationBar
