import axios from "axios";
import { useEffect, useState } from "react";
import { Button, Container, Form, FormLabel, Spinner, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

function GovEmployeeList({ apiURL }) {
    const [employees, setEmployees] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [search, setSearch] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        try {
            const fetchEmployees = async () => {
                const response = await axios.get(apiURL)
                setEmployees(response.data)
                setIsLoading(false)
            }

            fetchEmployees()
        } catch (error) {
            console.log(error)
        }
    }, []);

    const deleteEmployee = async (id) => {
        await axios.delete(`${apiURL}/${id}`)
        navigate("/listarServidores")
    };

    const renderEmployees = employees
        .filter((employee) => employee.name.toLowerCase().includes(search.toLowerCase()))
        .map((employee) => {
            return (
               /* <tr key={employee._id}>
                    <td>{employee.name}</td>
                    <td>{employee.email}</td>
                    <td>{employee.department}</td>
                    <td>{employee.status}</td>
                    <td>
                        <Button variant="info" size="sm">
                            <Link className="nav-link" to={`/listarServidor/${employee._id}`}>Detalhar</Link>
                        </Button>
                        <Button variant="info" size="sm">
                            <Link className="nav-link" to={`/listarServidor/${employee._id}`}>Alterar</Link>
                        </Button>
                        <Button variant="danger" size="sm" onClick={() => deleteEmployee(employee._id)}>Excluir</Button>
                    </td>
                </tr>
                <div className="col-5">
                  <div className="list-group">
                     <Link to="/"><img src="" alt="" width="72" height="54"/>{employee.name}</Link>
                  </div>
                </div> */
                <article className="artigo">
                <section className="card">
                    <img src="" alt=""/>  
                </section>
                <section className="card">
                    <h2><b>First name:</b>{employee.name}</h2>
                    <h2><b>Last name:</b>{employee.email}</h2>
                    <h2><b>Gender:</b>{employee.email}</h2>
                    <h2><b>Height:</b>{employee.email}</h2>
                    <h2><b>Birth:</b>{employee.email}</h2>
                </section> 
            </article>
            )
        })

    return (
        <Container>
            <Form className="my-4">
                <FormLabel>
                    <h3>Total de Servidores:{employees.length}</h3>
                </FormLabel>
                <Form.Control
                    type="search"
                    placeholder="Procurar funcionário"
                    value={ search }
                    onChange={ (e) => setSearch(e.target.value) }
                />
                <Button variant="info" size="sm">
                        <Link className="nav-link" to="/cadastrarServidor">Novo Servidor</Link>
                </Button>
            </Form>
            {isLoading && <Spinner className="" animation="border" />}
            {!isLoading &&
                <Table className="mt-4" striped bordered hover>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Endereço de e-mail</th>
                            <th>Departamento</th>
                            <th>Status</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        { renderEmployees }
                    </tbody>
                </Table>
            }
        </Container>
    )
}

export default GovEmployeeList;
